INSERT INTO `question_sub_items` VALUES (1, 1, '请在下方作答 / Please write your answer below:', 'essay', NULL, '[\"A Book to Recommend\\nThe book I feel obliged to recommend to all of you is The Silk Roads: A New History of the World, written by Peter Frankopan, a world-acclaimed Oxford historian. The reading of this book could awaken you to a wider perspective on this bridge linking the East and the West.\\nThe groundbreaking book sets readers on an extraordinary journey along the Silk Roads, exploring the connections among people, trade, disease, war, and so on. It offers sharp insights into how the Silk Roads travels through history, connecting, building, and rebuilding the world we are living in. Add to that the context of the Belt and Road Initiative, and it makes great sense to read the book and learn about its history from different angles. Good news is that the book has a Chinese version, which will serve as a great help for you to understand its contents.\\nI give this book my highest recommendation and hope that you will enjoy it and share your ideas with me.\"]', '', 1.0, 999);
INSERT INTO `question_sub_items` VALUES (2, 2, 'Question 1', 'choice', '[{\"key\": \"A\", \"value\": \"A proposed policy allowing Africans to travel in Africa without a visa.\"}, {\"key\": \"B\", \"value\": \"An agreement among 13 African countries to set up a free-trade zone.\"}, {\"key\": \"C\", \"value\": \"A plan to invite all African countries to join the African Union.\"}, {\"key\": \"D\", \"value\": \"An important initiative to permit tourists to visit Africa without a visa.\"}]', '[\"A\"]', '主旨大意题。新闻开头指出,非洲领导人计划把非洲大陆变成一个没有边界的大陆,他们将引入单一护照政策,允许各国之间自由通行。下文中提到,非洲联盟希望废除非洲人访问其他非洲国家的签证要求。由此可知,新闻围绕一项能使非洲人无须签证即可在各国之间旅行的政策展开。', 1.0, 1);
INSERT INTO `question_sub_items` VALUES (3, 2, 'Question 2', 'choice', '[{\"key\": \"A\", \"value\": \"It will attract more investments from all over the world.\"}, {\"key\": \"B\", \"value\": \"It will help many African countries reduce trade deficits.\"}, {\"key\": \"C\", \"value\": \"It will reduce the cost of trade between African countries.\"}, {\"key\": \"D\", \"value\": \"It will allow Africans to play a bigger role in world trade.\"}]', '[\"C\"]', '细节推断题。新闻结尾处提到,非洲联盟计划在非洲大陆建立一项自由贸易协定,因为非洲大陆的贸易成本高于任何其他地区。由此推断,自由贸易协定可以降低非洲大陆贸易的成本。', 1.0, 2);
INSERT INTO `question_sub_items` VALUES (4, 2, 'Question 3', 'choice', '[{\"key\": \"A\", \"value\": \"Cooling down volcanic rock with sea water.\"}, {\"key\": \"B\", \"value\": \"Storing carbon dioxide underground as a gas.\"}, {\"key\": \"C\", \"value\": \"Capturing carbon dioxide and burying it under the sea.\"}, {\"key\": \"D\", \"value\": \"Pumping carbon dioxide underground to form stone.\"}]', '[\"D\"]', '细节辨认题。新闻开头指出,冰岛的研究人员发现了一种应对气候变化的新方法,即将二氧化碳用泵输送到地下,并将其变成石头。', 1.0, 3);
INSERT INTO `question_sub_items` VALUES (5, 2, 'Question 4', 'choice', '[{\"key\": \"A\", \"value\": \"Lack of burying ground.\"}, {\"key\": \"C\", \"value\": \"High consumption of water.\"}, {\"key\": \"B\", \"value\": \"Long time for processing.\"}, {\"key\": \"D\", \"value\": \"Enormous cost of energy.\"}]', '[\"C\"]', '细节辨认题。新闻最后提到,这种掩埋二氧化碳并将其变成石头的新技术有一个潜在的困难,每掩埋一吨二氧化碳需要二十五吨水。由此可知,这项新技术耗水量高。', 1.0, 4);
INSERT INTO `question_sub_items` VALUES (6, 2, 'Question 5', 'choice', '[{\"key\": \"A\", \"value\": \"It varies with the seasons.\"}, {\"key\": \"C\", \"value\": \"It has caused nationwide concern.\"}, {\"key\": \"B\", \"value\": \"It has been on the decline.\"}, {\"key\": \"D\", \"value\": \"It is ninety grams daily on average.\"}]', '[\"B\"]', '细节辨认题。新闻开头提到,法国全国磨坊主协会表示,法国人的面包消费量正在下降,他们比前年少吃了九克。', 1.0, 5);
INSERT INTO `question_sub_items` VALUES (7, 2, 'Question 6', 'choice', '[{\"key\": \"A\", \"value\": \"They emphasize food variety.\"}, {\"key\": \"C\", \"value\": \"They do more cooking than men.\"}, {\"key\": \"B\", \"value\": \"They prefer French stick loaves.\"}, {\"key\": \"D\", \"value\": \"They favor diets lower in calories.\"}]', '[\"D\"]', '细节推断题。新闻中提到,法国面包消费量下降的第一个原因是饮食的改变,尤其是向低热量饮食的转变。这可以解释为什么不同性别之间的消费差异如此之大。现在,男性平均每天大约吃140克面包,而女性每天大约吃80至90克。由此推断,法国女性喜欢低热量的饮食。', 1.0, 6);
INSERT INTO `question_sub_items` VALUES (8, 2, 'Question 7', 'choice', '[{\"key\": \"A\", \"value\": \"They bake more bread at home.\"}, {\"key\": \"C\", \"value\": \"They eat more fruit than they used to.\"}, {\"key\": \"B\", \"value\": \"They spend less time eating breakfast.\"}, {\"key\": \"D\", \"value\": \"They put jam instead of butter on bread.\"}]', '[\"B\"]', '细节辨认题。新闻中提到,法国面包消费量下降的第二个原因是这个国家的生活方式发生了变化,人们花在早餐上的时间减少了。', 1.0, 7);
INSERT INTO `question_sub_items` VALUES (9, 3, 'Question 8', 'choice', '[{\"key\": \"A\", \"value\": \"After the rush hour.\"}, {\"key\": \"C\", \"value\": \"As soon as possible.\"}, {\"key\": \"B\", \"value\": \"Early in the morning.\"}, {\"key\": \"D\", \"value\": \"Around lunch time.\"}]', '[\"A\"]', '细节辨认题。对话中,女士询问男士什么时候出发去伦敦,男士说想在高峰时间过后,如果可能的话他想避开人群,大概在10点钟左右。由此可知,男士想动身去伦敦的时间是高峰时段后。', 1.0, 8);
INSERT INTO `question_sub_items` VALUES (10, 3, 'Question 9', 'choice', '[{\"key\": \"A\", \"value\": \"Two first class seats.\"}, {\"key\": \"C\", \"value\": \"One window seat.\"}, {\"key\": \"B\", \"value\": \"Two seats together.\"}, {\"key\": \"D\", \"value\": \"One seat near the exit.\"}]', '[\"C\"]', '细节辨认题。女士询问男士想要一等座还是二等座后,男士选择二等座,并询问是否可以找个靠窗的座位。女士表示同意,并为他预订了相邻的座位。由此可知,男士的其他要求是找个靠窗的座位。', 1.0, 9);
INSERT INTO `question_sub_items` VALUES (11, 3, 'Question 10', 'choice', '[{\"key\": \"A\", \"value\": \"In the middle of the platform.\"}, {\"key\": \"B\", \"value\": \"At the far end of the platform.\"}, {\"key\": \"C\", \"value\": \"Behind the yellow line.\"}, {\"key\": \"D\", \"value\": \"In the waiting room.\"}]', '[\"B\"]', '细节辨认题。男士出行要带自行车,询问女士如何处理和是否额外收费的问题,女士表示不会额外收费,但需要在站台的尽头等车。', 1.0, 10);
INSERT INTO `question_sub_items` VALUES (12, 3, 'Question 11', 'choice', '[{\"key\": \"A\", \"value\": \"Wait in a queue for his turn.\"}, {\"key\": \"C\", \"value\": \"Pay an extra fee for the service.\"}, {\"key\": \"B\", \"value\": \"Go to the baggage claim area.\"}, {\"key\": \"D\", \"value\": \"Give the ticket to the train guard.\"}]', '[\"D\"]', '细节辨认题。对话中,女士告知男士到达伦敦时,把票交给列车长,列车长就会把自行车还给男士。', 1.0, 11);
INSERT INTO `question_sub_items` VALUES (13, 3, 'Question 12', 'choice', '[{\"key\": \"A\", \"value\": \"Almost all of them were operating at a deficit.\"}, {\"key\": \"B\", \"value\": \"They have all been turned into movie theatres.\"}, {\"key\": \"C\", \"value\": \"Nearly all of them closed down decades ago.\"}, {\"key\": \"D\", \"value\": \"They have adapted to meet fashion changes.\"}]', '[\"C\"]', '细节辨认题。对话开头,女士指出2 50年代之前英国曾经有很多帽子工厂。但时尚改变了,大多数人不再戴帽子。因此,几乎所有这些工厂都不得不关闭。由此可知,英国的帽子工厂几十年前就倒闭了。', 1.0, 12);
INSERT INTO `question_sub_items` VALUES (14, 3, 'Question 13', 'choice', '[{\"key\": \"A\", \"value\": \"It shows foreign movies exclusively.\"}, {\"key\": \"C\", \"value\": \"It is the oldest theatre in London.\"}, {\"key\": \"B\", \"value\": \"It shows movies from the 1950s.\"}, {\"key\": \"D\", \"value\": \"It is located next to a hat factory.\"}]', '[\"A\"]', '细节辨认题。对话中男士询问女士是什么让她经营的影院与众不同,女士回答说她的影院是英国唯一一家只放映外语电影的电影院。', 1.0, 13);
INSERT INTO `question_sub_items` VALUES (15, 3, 'Question 14', 'choice', '[{\"key\": \"A\", \"value\": \"They wear hats on social occasions.\"}, {\"key\": \"C\", \"value\": \"They enjoy watching foreign movies.\"}, {\"key\": \"B\", \"value\": \"They don\'t speak foreign languages.\"}, {\"key\": \"D\", \"value\": \"They don\'t go to movie theatres often.\"}]', '[\"B\"]', '细节辨认题。男士得知女士的电影院只放映外语电影时询问看外语电影的需求量,因为在他看来,大多数伦敦人不会说外语。', 1.0, 14);
INSERT INTO `question_sub_items` VALUES (16, 3, 'Question 15', 'choice', '[{\"key\": \"A\", \"value\": \"They are meant mostly for immigrants.\"}, {\"key\": \"B\", \"value\": \"They attract large crowds of young Londoners.\"}, {\"key\": \"C\", \"value\": \"They are hard for English people to appreciate.\"}, {\"key\": \"D\", \"value\": \"They have an English translation on the screen.\"}]', '[\"D\"]', '细节辨认题。对话末尾,女士表示很多人对外语电影感兴趣,并补充道,所有电影的屏幕底部都有英文字幕,即使听不懂演员们说的话,仍然可以读懂。', 1.0, 15);
INSERT INTO `question_sub_items` VALUES (17, 4, 'Question 16', 'choice', '[{\"key\": \"A\", \"value\": \"She doesn\'t think smartphones can replace tablets.\"}, {\"key\": \"B\", \"value\": \"She incorporates smartphones into her teaching.\"}, {\"key\": \"C\", \"value\": \"She cannot stop children using smartphones.\"}, {\"key\": \"D\", \"value\": \"She regards smartphones as a distraction.\"}]', '[\"B\"]', '细节辨认题。短文中指出,中学数学教师阿斯特丽德·纳特利将智能手机融入教学。她认为当学生使用手机进行研究时,他们会发现手中有一个强大的工具,如果我们阻止孩子使用手机,我们就是在拒绝他们关心的东西。', 1.0, 16);
INSERT INTO `question_sub_items` VALUES (18, 4, 'Question 17', 'choice', '[{\"key\": \"A\", \"value\": \"To enforce school discipline.\"}, {\"key\": \"C\", \"value\": \"To help children grow up to be professionals.\"}, {\"key\": \"B\", \"value\": \"To make students concentrate.\"}, {\"key\": \"D\", \"value\": \"To cultivate children\'s good study habits.\"}]', '[\"C\"]', '细节辨认题。短文提到,注册护士海伦·洛克哈特支持全面禁止在课堂上使用智能手机。她在工作中不允许使用手机,而且必须上交手机,否则就会受到纪律处分,从而影响她的职业生涯。她认为我们需要教孩子们如何努力成为他们心目中的职业人士。', 1.0, 17);
INSERT INTO `question_sub_items` VALUES (19, 4, 'Question 18', 'choice', '[{\"key\": \"A\", \"value\": \"Use books and pens only.\"}, {\"key\": \"C\", \"value\": \"Make full use of electronic devices.\"}, {\"key\": \"B\", \"value\": \"Cut down their screen time.\"}, {\"key\": \"D\", \"value\": \"Learn to use the internet for research.\"}]', '[\"A\"]', '细节推断题。短文末尾提到,英语教师理查德·斯通认为学校和家庭应该区分明确,学生应该在这两种环境中做不同的事情。应该把手机和平板放在家里,在课堂上展示一个不同的世界———只有书本和钢笔的世界。', 1.0, 18);
INSERT INTO `question_sub_items` VALUES (20, 4, 'Question 19', 'choice', '[{\"key\": \"A\", \"value\": \"By cleaning the kitchen floor.\"}, {\"key\": \"C\", \"value\": \"By helping her mother do the dishes.\"}, {\"key\": \"B\", \"value\": \"By looking after her baby brother.\"}, {\"key\": \"D\", \"value\": \"By helping her brother wash windows.\"}]', '[\"D\"]', '细节辨认题。短文开头提到,一个六岁的女孩凯特帮助哥哥清洗窗户,获得了母亲承诺的奖励————个她想要的紫色玩偶。', 1.0, 19);
INSERT INTO `question_sub_items` VALUES (21, 4, 'Question 20', 'choice', '[{\"key\": \"A\", \"value\": \"She ordered a large number of dolls.\"}, {\"key\": \"B\", \"value\": \"She checked if a purple doll was ordered.\"}, {\"key\": \"C\", \"value\": \"She mistakenly canceled her mom\'s order.\"}, {\"key\": \"D\", \"value\": \"She ordered a more expensive doll instead.\"}]', '[\"A\"]', '细节推断题。短文中提到,凯特想了解发货状态,母亲打开网页后便走出了房间。凯特在查看网页时注意到一些可订购的其他玩偶的建议,她点了几下鼠标,将价值350多美元的玩偶下单后就跑去玩了。由此可知,凯特在母亲离开房间后从网上订购了大量的玩偶。', 1.0, 20);
INSERT INTO `question_sub_items` VALUES (22, 4, 'Question 21', 'choice', '[{\"key\": \"A\", \"value\": \"They asked the children\'s hospital to pick them.\"}, {\"key\": \"B\", \"value\": \"They took all of them to the children\'s hospital.\"}, {\"key\": \"C\", \"value\": \"They opened all of them one by one.\"}, {\"key\": \"D\", \"value\": \"They discussed where to keep them.\"}]', '[\"B\"]', '细节辨认题。短文中提到,凯特的父亲建议把这些玩偶送给当地的儿童医院,而不是退回。所以第二周凯特的家人把玩偶装车上,带到了儿童医院的游戏室。', 1.0, 21);
INSERT INTO `question_sub_items` VALUES (23, 4, 'Question 22', 'choice', '[{\"key\": \"A\", \"value\": \"The time one starts school.\"}, {\"key\": \"C\", \"value\": \"The school that one attends.\"}, {\"key\": \"B\", \"value\": \"The classroom atmosphere.\"}, {\"key\": \"D\", \"value\": \"The relationship with classmates.\"}]', '[\"A\"]', '细节辨认题。短文开头提到,澳大利亚的一项研究表明,一个人的出生日期会影响其有多成功。一个人开始上学的时间对他们的生活有着持久的影响。', 1.0, 22);
INSERT INTO `question_sub_items` VALUES (24, 4, 'Question 23', 'choice', '[{\"key\": \"A\", \"value\": \"To help parents decide when to send their children to school.\"}, {\"key\": \"B\", \"value\": \"To find causes for differences in the participants\' performance.\"}, {\"key\": \"C\", \"value\": \"To identify students having potential to be professional athletes.\"}, {\"key\": \"D\", \"value\": \"To offer constructive suggestions for making educational policies.\"}]', '[\"B\"]', '细节辨认题。短文中提到,之前的研究发现,年龄大的学生成绩更好,他们也更有可能上大学,成为职业运动员、首席执行官甚至有名的政治家。但澳大利亚的研究人员希望发现是什么导致了这些差异。', 1.0, 23);
INSERT INTO `question_sub_items` VALUES (25, 4, 'Question 24', 'choice', '[{\"key\": \"A\", \"value\": \"Political leaders.\"}, {\"key\": \"C\", \"value\": \"Professional athletes.\"}, {\"key\": \"B\", \"value\": \"Financial analysts.\"}, {\"key\": \"D\", \"value\": \"High-school students.\"}]', '[\"D\"]', '细节辨认题。短文中提到,在第一项研究中,600多名高中生接受了关于他们的冒险倾向和自信倾向的调查。', 1.0, 24);
INSERT INTO `question_sub_items` VALUES (26, 4, 'Question 25', 'choice', '[{\"key\": \"A\", \"value\": \"Leadership ability.\"}, {\"key\": \"C\", \"value\": \"Risk-taking.\"}, {\"key\": \"B\", \"value\": \"Commitment to work.\"}, {\"key\": \"D\", \"value\": \"Self-confidence.\"}]', '[\"C\"]', '细节推断题。短文最后提到,尽管冒险可能被认为是一种负面特征,但这也是成功人士的一个特征。由此可知,冒险是成功人士的一个特征。', 1.0, 25);
INSERT INTO `question_sub_items` VALUES (27, 5, '___26___', 'choice', '[{\"key\": \"A\", \"value\": \"wore\"}, {\"key\": \"I\", \"value\": \"intense\"}, {\"key\": \"B\", \"value\": \"wildlife\"}, {\"key\": \"J\", \"value\": \"human\"}, {\"key\": \"C\", \"value\": \"suspect\"}, {\"key\": \"K\", \"value\": \"effective\"}, {\"key\": \"D\", \"value\": \"struck\"}, {\"key\": \"L\", \"value\": \"delight\"}, {\"key\": \"E\", \"value\": \"similar\"}, {\"key\": \"M\", \"value\": \"colorful\"}, {\"key\": \"F\", \"value\": \"quantity\"}, {\"key\": \"N\", \"value\": \"bore\"}, {\"key\": \"G\", \"value\": \"overthrown\"}, {\"key\": \"O\", \"value\": \"alike\"}, {\"key\": \"H\", \"value\": \"mess\"}]', '[\"O\"]', '【语法判断】空格前是本句的主语,空格后是谓语动词,本句成分完整,因此空格处应填入副词作状语。【语义判断】空格所在句的意思是:作家、艺术家和儿童梦想着这样一份来自大海的礼物。由前一句可知,来自大海的礼物指的是含有消息的漂流瓶,而作家、艺术家和儿童都擅长想象,具有相似之处,而且他们都想要这样一份礼物,因此本空应填入含有“相似地,同样地”意义的副词。且备选词中只有一个副词,由此也可确定答案。', 1.0, 26);
INSERT INTO `question_sub_items` VALUES (28, 5, '___27___', 'choice', '[{\"key\": \"A\", \"value\": \"wore\"}, {\"key\": \"I\", \"value\": \"intense\"}, {\"key\": \"B\", \"value\": \"wildlife\"}, {\"key\": \"J\", \"value\": \"human\"}, {\"key\": \"C\", \"value\": \"suspect\"}, {\"key\": \"K\", \"value\": \"effective\"}, {\"key\": \"D\", \"value\": \"struck\"}, {\"key\": \"L\", \"value\": \"delight\"}, {\"key\": \"E\", \"value\": \"similar\"}, {\"key\": \"M\", \"value\": \"colorful\"}, {\"key\": \"F\", \"value\": \"quantity\"}, {\"key\": \"N\", \"value\": \"bore\"}, {\"key\": \"G\", \"value\": \"overthrown\"}, {\"key\": \"O\", \"value\": \"alike\"}, {\"key\": \"H\", \"value\": \"mess\"}]', '[\"D\"]', '【语法判断】空格前的 itself 是宾语,后面是by 引导的介词短语,因此本空应填入动词的过去分词。【语义判断】空格所在句的意思是:该岛位于北海海岸附近,发现自己被大量涌入的彩色塑料蛋_。 invasion 本意为“入侵,侵略”,为了语义协调一致,所以本空应填入含有“袭击”等意义的动词过去分词。备选的其他动词过去分词均没有此含义,因此排除。', 1.0, 27);
INSERT INTO `question_sub_items` VALUES (29, 5, '___28___', 'choice', '[{\"key\": \"A\", \"value\": \"wore\"}, {\"key\": \"I\", \"value\": \"intense\"}, {\"key\": \"B\", \"value\": \"wildlife\"}, {\"key\": \"J\", \"value\": \"human\"}, {\"key\": \"C\", \"value\": \"suspect\"}, {\"key\": \"K\", \"value\": \"effective\"}, {\"key\": \"D\", \"value\": \"struck\"}, {\"key\": \"L\", \"value\": \"delight\"}, {\"key\": \"E\", \"value\": \"similar\"}, {\"key\": \"M\", \"value\": \"colorful\"}, {\"key\": \"F\", \"value\": \"quantity\"}, {\"key\": \"N\", \"value\": \"bore\"}, {\"key\": \"G\", \"value\": \"overthrown\"}, {\"key\": \"O\", \"value\": \"alike\"}, {\"key\": \"H\", \"value\": \"mess\"}]', '[\"L\"]', '【语法判断】空格前有定冠词 the,后面是 of 引导的介词短语,因此本空应填入名词。【语义判断】空格所在句的意思是:该岛位于北海海岸附近,发现自己被大量涌入的彩色塑料蛋包围了——这让当地的孩子们非常,因为蛋里有玩具。由句意可知,玩具会让孩子们高兴,因此本空应填入含有“高兴,欢喜”意义的名词。备选的其他名词均没有此含义,因此排除。', 1.0, 28);
