INSERT INTO `question_sub_items` VALUES (130, 32, 'According to paragraph 2, why are monarch butterflies colored black and orange?', 'choice', '[{\"key\": \"A\", \"value\": \"To look like poisonous plants\"}, {\"key\": \"B\", \"value\": \"To blend in with plant life found in their habitat\"}, {\"key\": \"C\", \"value\": \"To advertise their transition from the caterpillar stage\"}, {\"key\": \"D\", \"value\": \"To signal to potential attackers that they contain poisons\"}]', '[\"D\"]', NULL, 1.0, 4);
INSERT INTO `question_sub_items` VALUES (131, 32, 'What can be inferred from paragraph 2 about the toxic substances stored in the bodies of monarch butterflies?', 'choice', '[{\"key\": \"A\", \"value\": \"They are sometimes toxic to humans as well as to birds\"}, {\"key\": \"B\", \"value\": \"They are likely to kill inexperienced birds but not experienced birds\"}, {\"key\": \"C\", \"value\": \"They are usually vomited by birds before they can cause death.\"}, {\"key\": \"D\", \"value\": \"They usually have no effect on birds when eaten in small amounts.\"}]', '[\"C\"]', NULL, 1.0, 5);
INSERT INTO `question_sub_items` VALUES (132, 33, 'In paragraph 3, why does the author discuss an experiment in which researchers raised monarch caterpillars on two different species of milkweed?', 'choice', '[{\"key\": \"A\", \"value\": \"To prove that monarchs are able to consume plants that are poisonous to other species\"}, {\"key\": \"B\", \"value\": \"To support the claim that consuming poisonous milkweed is advantageous to monarchs\"}, {\"key\": \"C\", \"value\": \"To determine the amount of poison that is toxic to monarch butterflies\"}, {\"key\": \"D\", \"value\": \"To argue that most birds can consume poisonous insects without dying\"}]', '[\"B\"]', NULL, 1.0, 6);
INSERT INTO `question_sub_items` VALUES (133, 33, 'According to paragraph 3, jays that consumed toxin-containing monarchs reacted in all of the following ways EXCEPT by', 'choice', '[{\"key\": \"A\", \"value\": \"demonstrating symptoms of illness\"}, {\"key\": \"B\", \"value\": \"refusing to eat other toxin monarchs\"}, {\"key\": \"C\", \"value\": \"consuming only nontoxic monarchs in the future\"}, {\"key\": \"D\", \"value\": \"fluffing out their feathers immediately after consuming\"}]', '[\"C\"]', NULL, 1.0, 7);
INSERT INTO `question_sub_items` VALUES (134, 34, 'What can be inferred from paragraph 4 about protective systems based on deadly poisons?', 'choice', '[{\"key\": \"A\", \"value\": \"They are less common than nondeadly systems in nature.\"}, {\"key\": \"B\", \"value\": \"They are more effective than systems based on nondeadly poisons.\"}, {\"key\": \"C\", \"value\": \"They are less common in insects than in other types of animals.\"}, {\"key\": \"D\", \"value\": \"They are most likely to be found in nonterritorial species.\"}]', '[\"A\"]', NULL, 1.0, 8);
