INSERT INTO `question_sub_items` VALUES (138, 38, 'According to paragraph 2, what was Sir Harold Jeffreys’ contribution to the debate about continental drift?', 'choice', '[{\"key\": \"A\", \"value\": \"He supported Alfred Wegener’s explanation for the way the continental outlines fit together.\"}, {\"key\": \"B\", \"value\": \"He demonstrated that continental drift was influenced by Earth’s rotation.\"}, {\"key\": \"C\", \"value\": \"He showed that Frank B. Taylor’s theory of the cause of continental drift was not possible.\"}, {\"key\": \"D\", \"value\": \"He combined Frank B. Taylor’s theory and Alfred Wegener’s theory.\"}]', '[\"C\"]', NULL, 1.0, 2);
INSERT INTO `question_sub_items` VALUES (139, 39, 'Which of the following can be inferred from paragraph 3 about Alfred Wegener\'s book ‘The Origins of the Continents and Oceans’?', 'choice', '[{\"key\": \"A\", \"value\": \"It did not explain how the continents moved.\"}, {\"key\": \"B\", \"value\": \"It showed why the idea of continental drift was improbable.\"}, {\"key\": \"C\", \"value\": \"It was the first scientific source to mention the similarities between fossilized plants and animals in South America and Africa.\"}, {\"key\": \"D\", \"value\": \"It provided several new examples of places where the continents fit together.\"}]', '[\"A\"]', NULL, 1.0, 3);
INSERT INTO `question_sub_items` VALUES (140, 40, 'Which of the sentences below best expresses the essential information in the highlighted sentence in the passage? Incorrect choices change the meaning in important ways or leave out essential information.', 'choice', '[{\"key\": \"A\", \"value\": \"Alexander Du Toit published Our Wandering Continents in 1937 to disagree with the view that continents could move.\"}, {\"key\": \"B\", \"value\": \"Alexander Du Toit agreed completely with Wegener\'s view that the continents could move.\"}, {\"key\": \"C\", \"value\": \"In Our Wandering Continents, Alexander Du Toit agreed with Wegener on all matters concerning the movement of continents.\"}, {\"key\": \"D\", \"value\": \"The opposition to the prevailing view that the continents could not move led Alexander Du Toit to publish Our Wandering Continents in 1937.\"}]', '[\"B\"]', NULL, 1.0, 4);
INSERT INTO `question_sub_items` VALUES (141, 40, 'According to paragraph 4, the geologists who supported Wegener\'s theory of the separation of the continents', 'choice', '[{\"key\": \"A\", \"value\": \"were unaware of earlier theories explaining the way the continental outlines of South America and Africa fit together\"}, {\"key\": \"B\", \"value\": \"were directly familiar with the geological evidence in Brazil and South Africa\"}, {\"key\": \"C\", \"value\": \"agreed with the prevailing view in Europe and North America\"}, {\"key\": \"D\", \"value\": \"discovered evidence in Europe and North America that supported the theory\"}]', '[\"B\"]', NULL, 1.0, 5);
INSERT INTO `question_sub_items` VALUES (142, 40, 'Which of the following best describes the role of paragraph 4 in the passage?', 'choice', '[{\"key\": \"A\", \"value\": \"It presents evidence provided by other scientists that supports Wegener’s theory.\"}, {\"key\": \"B\", \"value\": \"It presents the reactions of other scientists to Wegener’s book.\"}, {\"key\": \"C\", \"value\": \"It describes how Wegener’s book inspired other scientists to find evidence of continental drift in South Africa and Brazil.\"}, {\"key\": \"D\", \"value\": \"It describes which parts of Wegener’s theory were accepted by other scientists and which parts were rejected.\"}]', '[\"B\"]', NULL, 1.0, 6);
