INSERT INTO `question_sub_items` VALUES (144, 42, 'According to paragraph 6, what was Pangaea?', 'choice', '[{\"key\": \"A\", \"value\": \"Wegener’s name for the landmass that broke apart to form most of today’s continent.\"}, {\"key\": \"B\", \"value\": \"Wegener’s name for the small area where the plant and animal fossils he studied lived.\"}, {\"key\": \"C\", \"value\": \"Wegener’s name for the period in Earth’s history 250 million to 300 million years ago.\"}, {\"key\": \"D\", \"value\": \"Wegener’s name for the period in Earth’s history when the plants and animals he studied were alive.\"}]', '[\"A\"]', NULL, 1.0, 8);
