INSERT INTO `question_sub_items` VALUES (150, 46, 'According to paragraph 2, advances in camera technology had which of the following consequences?', 'choice', '[{\"key\": \"A\", \"value\": \"Camera use became specialized and was practiced by professionals only\"}, {\"key\": \"B\", \"value\": \"Photographers began to create and show photographs that looked like traditions, paintings.\"}, {\"key\": \"C\", \"value\": \"Photography became less expensive and more available to ordinary people.\"}, {\"key\": \"D\", \"value\": \"Most painters gave up their craft and made their living from photography\"}]', '[\"C\"]', NULL, 1.0, 4);
