INSERT INTO `question_sub_items` VALUES (152, 47, 'In paragraph 3, why does the author discuss William Holman Hunt’s trip to Palestine?', 'choice', '[{\"key\": \"A\", \"value\": \"To illustrate the importance some artists placed on correctly reproducing the world as it actually is\"}, {\"key\": \"B\", \"value\": \"To explain why some nineteenth-century English artists looked to Renaissance Italy for models\"}, {\"key\": \"C\", \"value\": \"To explain why Pre-Raphaelites were less concerned than earlier artists about composition and perspective\"}, {\"key\": \"D\", \"value\": \"To show that English painters were more enthusiastic than French painters about applying the methods of photography\"}]', '[\"A\"]', NULL, 1.0, 6);
